import pytest

from telliot_feeds.feeds.bct_usd_feed import bct_usd_median_feed


@pytest.mark.asyncio
async def test_fetch_price(mock_price_feed):
    mock_prices = [1200.50, 1205.25, 1202.75]
    mock_price_feed(bct_usd_median_feed, mock_prices)
    (value, _) = await bct_usd_median_feed.source.fetch_new_datapoint()
    assert value > 0
    print(value)


