import pytest

from telliot_feeds.feeds.idle_usd_feed import idle_usd_median_feed


@pytest.mark.asyncio
async def test_fetch_price(mock_price_feed):
    mock_prices = [1200.50]
    mock_price_feed(idle_usd_median_feed, mock_prices)
    (value, _) = await idle_usd_median_feed.source.fetch_new_datapoint()
    assert value > 0
    print(value)


