import pytest

from telliot_feeds.feeds.matic_usd_feed import matic_usd_median_feed


@pytest.mark.asyncio
async def test_fetch_price(mock_price_feed):
    mock_prices = [0.85, 0.87, 0.86]
    mock_price_feed(matic_usd_median_feed, mock_prices)
    (value, _) = await matic_usd_median_feed.source.fetch_new_datapoint()
    assert value > 0
    print(value)


