import pytest

from telliot_feeds.feeds.ric_usd_feed import ric_usd_median_feed


@pytest.mark.asyncio
async def test_fetch_price(mock_price_feed):
    mock_prices = [1200.50]
    mock_price_feed(ric_usd_median_feed, mock_prices)
    (value, _) = await ric_usd_median_feed.source.fetch_new_datapoint()
    assert value > 0
    print(value)


